set input = "" set validated = 0 write !!, "This setup will help you to configure a git integration." write !, "Please fill out fields when asked:", !
// git-diff tool configuration set defaults(2) = "{VCS} --work-tree={P1} --git-dir={P1}{SLASH}.git diff --name-status --diff-filter={P2} {P3} -- {P4} 2> ""{P5}"" > ""{P6}""" set defaults(3) = "{VCS} --work-tree={P1} --git-dir={P1}{SLASH}.git add {P2}" set defaults(4) = "{VCS} --work-tree={P1} --git-dir={P1}{SLASH}.git log --pretty=format:""%H||%an||%ae||%ad||at"" --date=relative 2> ""{P2}"" > ""{P3}"""
while 1 { if input = -1 write !, "Aborted." quit if '$get(validated(1), 0) { set input(1) = "" write !, "git.exe full path: " read input(1)
if input(1) = -1 set input = 1
set validated(1) = (##class(%File).Exists(input(1)) && (##class(%File).GetFilename(input(1)) = "git.exe")) if 'validated(1) { write !, "Invalid path, you must specify the path for your git.exe." continue } }
if '$get(validated(2), 0) { set input(2) = "" write !, "diff command (or empty to use default): " read input(2)
if input(2) = -1 set input = 1 if input(2) = "" set input(2) = defaults(2)
set validated(2) = 1 }
if '$get(validated(3), 0) { set input(3) = "" write !, "add command (or empty to use default):" read input(3)
if input(3) = -1 set input = 1 if input(3) = "" set input(3) = defaults(3)
set validated(3) = 1 }
if validated(1) && validated(2) && validated(3) quit }
do ##class(Port.SourceControl.Config).SetVCSPath(input(1)) do ##class(Port.SourceControl.Config).SetVCSCommand("diff", input(2)) do ##class(Port.SourceControl.Config).SetVCSCommand("add", input(3)) do ##class(Port.SourceControl.Config).SetVCSCommand("log", defaults(4)) do ##class(Port.SourceControl.Config).RegisterVCSHandler($classname($this)) do ##class(Port.SourceControl.Config).EnableVCS() write !!, "Done. Git is now integrated with the source control hook." quit $$$OK

